-- Create users table for employees and managers
create table if not exists public.users (
  id uuid primary key default gen_random_uuid(),
  username text unique not null,
  name text not null,
  email text unique not null,
  job_title text not null,
  phone_number text,
  role text not null check (role in ('employee', 'manager')),
  created_at timestamp with time zone default now(),
  updated_at timestamp with time zone default now()
);

-- Create announcements table
create table if not exists public.announcements (
  id uuid primary key default gen_random_uuid(),
  title text not null,
  content text not null,
  created_by uuid references public.users(id),
  created_at timestamp with time zone default now(),
  updated_at timestamp with time zone default now()
);

-- Create events table
create table if not exists public.events (
  id uuid primary key default gen_random_uuid(),
  title text not null,
  description text,
  event_date date not null,
  start_time time,
  end_time time,
  created_by uuid references public.users(id),
  created_at timestamp with time zone default now(),
  updated_at timestamp with time zone default now()
);

-- Create weekly_specials table
create table if not exists public.weekly_specials (
  id uuid primary key default gen_random_uuid(),
  day_of_week text not null check (day_of_week in ('Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday')),
  name text not null,
  description text,
  price text not null,
  created_by uuid references public.users(id),
  created_at timestamp with time zone default now(),
  updated_at timestamp with time zone default now()
);

-- Enable RLS on all tables
alter table public.users enable row level security;
alter table public.announcements enable row level security;
alter table public.events enable row level security;
alter table public.weekly_specials enable row level security;

-- RLS Policies for users table
create policy "Users can view all users"
  on public.users
  for select
  to authenticated
  using (true);

create policy "Only managers can insert users"
  on public.users
  for insert
  to authenticated
  with check (
    exists (
      select 1 from public.users
      where id = (select auth.uid())
      and role = 'manager'
    )
  );

create policy "Only managers can update users"
  on public.users
  for update
  to authenticated
  using (
    exists (
      select 1 from public.users
      where id = (select auth.uid())
      and role = 'manager'
    )
  );

create policy "Only managers can delete users"
  on public.users
  for delete
  to authenticated
  using (
    exists (
      select 1 from public.users
      where id = (select auth.uid())
      and role = 'manager'
    )
  );

-- RLS Policies for announcements table
create policy "Everyone can view announcements"
  on public.announcements
  for select
  to authenticated
  using (true);

create policy "Only managers can insert announcements"
  on public.announcements
  for insert
  to authenticated
  with check (
    exists (
      select 1 from public.users
      where id = (select auth.uid())
      and role = 'manager'
    )
  );

create policy "Only managers can update announcements"
  on public.announcements
  for update
  to authenticated
  using (
    exists (
      select 1 from public.users
      where id = (select auth.uid())
      and role = 'manager'
    )
  );

create policy "Only managers can delete announcements"
  on public.announcements
  for delete
  to authenticated
  using (
    exists (
      select 1 from public.users
      where id = (select auth.uid())
      and role = 'manager'
    )
  );

-- RLS Policies for events table
create policy "Everyone can view events"
  on public.events
  for select
  to authenticated
  using (true);

create policy "Only managers can insert events"
  on public.events
  for insert
  to authenticated
  with check (
    exists (
      select 1 from public.users
      where id = (select auth.uid())
      and role = 'manager'
    )
  );

create policy "Only managers can update events"
  on public.events
  for update
  to authenticated
  using (
    exists (
      select 1 from public.users
      where id = (select auth.uid())
      and role = 'manager'
    )
  );

create policy "Only managers can delete events"
  on public.events
  for delete
  to authenticated
  using (
    exists (
      select 1 from public.users
      where id = (select auth.uid())
      and role = 'manager'
    )
  );

-- RLS Policies for weekly_specials table
create policy "Everyone can view weekly specials"
  on public.weekly_specials
  for select
  to authenticated
  using (true);

create policy "Only managers can insert weekly specials"
  on public.weekly_specials
  for insert
  to authenticated
  with check (
    exists (
      select 1 from public.users
      where id = (select auth.uid())
      and role = 'manager'
    )
  );

create policy "Only managers can update weekly specials"
  on public.weekly_specials
  for update
  to authenticated
  using (
    exists (
      select 1 from public.users
      where id = (select auth.uid())
      and role = 'manager'
    )
  );

create policy "Only managers can delete weekly specials"
  on public.weekly_specials
  for delete
  to authenticated
  using (
    exists (
      select 1 from public.users
      where id = (select auth.uid())
      and role = 'manager'
    )
  );

-- Create indexes for better performance
create index if not exists idx_users_username on public.users(username);
create index if not exists idx_users_role on public.users(role);
create index if not exists idx_announcements_created_at on public.announcements(created_at desc);
create index if not exists idx_events_event_date on public.events(event_date);
create index if not exists idx_weekly_specials_day on public.weekly_specials(day_of_week);

