SELECT * FROM pg_policies WHERE schemaname = 'storage' AND tablename = 'objects';

-- Add password field to users table
ALTER TABLE public.users ADD COLUMN IF NOT EXISTS password_hash TEXT NOT NULL DEFAULT crypt('boathouseconnect', gen_salt('bf'));

-- Enable pgcrypto extension if not already enabled
CREATE EXTENSION IF NOT EXISTS pgcrypto;

-- Update existing users to have the default password
UPDATE public.users 
SET password_hash = crypt('boathouseconnect', gen_salt('bf'))
WHERE password_hash IS NULL OR password_hash = '';

-- Function to verify password
CREATE OR REPLACE FUNCTION verify_password(user_id UUID, password TEXT)
RETURNS BOOLEAN
LANGUAGE plpgsql
SECURITY DEFINER
AS $$
DECLARE
  stored_hash TEXT;
BEGIN
  SELECT password_hash INTO stored_hash
  FROM public.users
  WHERE id = user_id;

  IF stored_hash IS NULL THEN
    RETURN FALSE;
  END IF;

  RETURN stored_hash = crypt(password, stored_hash);
END;
$$;

-- Function to update password
CREATE OR REPLACE FUNCTION update_password(user_id UUID, new_password TEXT)
RETURNS VOID
LANGUAGE plpgsql
SECURITY DEFINER
AS $$
BEGIN
  UPDATE public.users
  SET password_hash = crypt(new_password, gen_salt('bf')),
      updated_at = now()
  WHERE id = user_id;
END;
$$;

-- Grant execute permissions to authenticated users
GRANT EXECUTE ON FUNCTION verify_password(UUID, TEXT) TO anon, authenticated;
GRANT EXECUTE ON FUNCTION update_password(UUID, TEXT) TO anon, authenticated;

