-- Create storage bucket for guides and training files
INSERT INTO storage.buckets (id, name, public)
VALUES ('guides-and-training', 'guides-and-training', true)
ON CONFLICT (id) DO NOTHING;

-- Enable RLS on storage.objects (if not already enabled)
ALTER TABLE storage.objects ENABLE ROW LEVEL SECURITY;

-- Drop existing policies if they exist
DROP POLICY IF EXISTS "Allow authenticated users to read guides files" ON storage.objects;
DROP POLICY IF EXISTS "Allow managers to upload guides files" ON storage.objects;
DROP POLICY IF EXISTS "Allow managers to update guides files" ON storage.objects;
DROP POLICY IF EXISTS "Allow managers to delete guides files" ON storage.objects;

-- Policy: Allow authenticated users to read files from guides-and-training bucket
CREATE POLICY "Allow authenticated users to read guides files"
ON storage.objects FOR SELECT
TO authenticated
USING (bucket_id = 'guides-and-training');

-- Policy: Allow managers to upload files to guides-and-training bucket
CREATE POLICY "Allow managers to upload guides files"
ON storage.objects FOR INSERT
TO authenticated
WITH CHECK (
  bucket_id = 'guides-and-training' AND
  EXISTS (
    SELECT 1 FROM public.users
    WHERE users.id = auth.uid()
    AND users.role = 'manager'
  )
);

-- Policy: Allow managers to update files in guides-and-training bucket
CREATE POLICY "Allow managers to update guides files"
ON storage.objects FOR UPDATE
TO authenticated
USING (
  bucket_id = 'guides-and-training' AND
  EXISTS (
    SELECT 1 FROM public.users
    WHERE users.id = auth.uid()
    AND users.role = 'manager'
  )
);

-- Policy: Allow managers to delete files from guides-and-training bucket
CREATE POLICY "Allow managers to delete guides files"
ON storage.objects FOR DELETE
TO authenticated
USING (
  bucket_id = 'guides-and-training' AND
  EXISTS (
    SELECT 1 FROM public.users
    WHERE users.id = auth.uid()
    AND users.role = 'manager'
  )
);

-- Drop existing policies for guides_and_training table
DROP POLICY IF EXISTS "Allow authenticated users to read guides" ON public.guides_and_training;
DROP POLICY IF EXISTS "Allow managers to insert guides" ON public.guides_and_training;
DROP POLICY IF EXISTS "Allow managers to update guides" ON public.guides_and_training;
DROP POLICY IF EXISTS "Allow managers to delete guides" ON public.guides_and_training;

-- RLS policies for guides_and_training table
-- Policy: Allow all authenticated users to read guides
CREATE POLICY "Allow authenticated users to read guides"
ON public.guides_and_training FOR SELECT
TO authenticated
USING (is_active = true);

-- Policy: Allow managers to insert guides
CREATE POLICY "Allow managers to insert guides"
ON public.guides_and_training FOR INSERT
TO authenticated
WITH CHECK (
  EXISTS (
    SELECT 1 FROM public.users
    WHERE users.id = auth.uid()
    AND users.role = 'manager'
  )
);

-- Policy: Allow managers to update guides
CREATE POLICY "Allow managers to update guides"
ON public.guides_and_training FOR UPDATE
TO authenticated
USING (
  EXISTS (
    SELECT 1 FROM public.users
    WHERE users.id = auth.uid()
    AND users.role = 'manager'
  )
);

-- Policy: Allow managers to delete guides
CREATE POLICY "Allow managers to delete guides"
ON public.guides_and_training FOR DELETE
TO authenticated
USING (
  EXISTS (
    SELECT 1 FROM public.users
    WHERE users.id = auth.uid()
    AND users.role = 'manager'
  )
);

-- Create storage bucket for guides and training files
INSERT INTO storage.buckets (id, name, public)
VALUES ('guides-and-training', 'guides-and-training', true)
ON CONFLICT (id) DO NOTHING;

-- Drop existing policies for guides_and_training table
DROP POLICY IF EXISTS "Allow authenticated users to read guides" ON public.guides_and_training;
DROP POLICY IF EXISTS "Allow managers to insert guides" ON public.guides_and_training;
DROP POLICY IF EXISTS "Allow managers to update guides" ON public.guides_and_training;
DROP POLICY IF EXISTS "Allow managers to delete guides" ON public.guides_and_training;

-- RLS policies for guides_and_training table
-- Policy: Allow all authenticated users to read guides
CREATE POLICY "Allow authenticated users to read guides"
ON public.guides_and_training FOR SELECT
TO authenticated
USING (is_active = true);

