  p_manager_id UUID,
  p_employee_id UUID,
  p_name TEXT,
  p_email TEXT,
  p_job_title TEXT,
  p_phone_number TEXT,
  p_role TEXT
)
RETURNS BOOLEAN
LANGUAGE plpgsql
SECURITY DEFINER
AS $$
DECLARE
  v_is_manager BOOLEAN;
BEGIN
  -- Check if the user making the request is a manager
  SELECT role = 'manager' INTO v_is_manager
  FROM users
  WHERE id = p_manager_id;

  IF NOT v_is_manager THEN
    RAISE EXCEPTION 'Only managers can update employee information';
  END IF;

  -- Update the employee
  UPDATE users
  SET 
    name = p_name,
    email = p_email,
    job_title = p_job_title,
    phone_number = p_phone_number,
    role = p_role,
    updated_at = NOW()
  WHERE id = p_employee_id;

  RETURN TRUE;
END;
$$;

-- Function to update transaction and recalculate user balance
CREATE OR REPLACE FUNCTION update_transaction_and_balance(
  p_manager_id UUID,
  p_transaction_id UUID,
  p_new_amount INTEGER,
  p_new_description TEXT
)
RETURNS BOOLEAN
LANGUAGE plpgsql
SECURITY DEFINER
AS $$
DECLARE
  v_is_manager BOOLEAN;
  v_old_amount INTEGER;
  v_user_id UUID;
  v_amount_difference INTEGER;
BEGIN
  -- Check if the user making the request is a manager
  SELECT role = 'manager' INTO v_is_manager
  FROM users
  WHERE id = p_manager_id;

  IF NOT v_is_manager THEN
    RAISE EXCEPTION 'Only managers can update transactions';
  END IF;

  -- Get the old transaction amount and user_id
  SELECT amount, user_id INTO v_old_amount, v_user_id
  FROM rewards_transactions
  WHERE id = p_transaction_id;

  IF v_old_amount IS NULL THEN
    RAISE EXCEPTION 'Transaction not found';
  END IF;

  -- Calculate the difference
  v_amount_difference := p_new_amount - v_old_amount;

  -- Update the transaction
  UPDATE rewards_transactions
  SET 
    amount = p_new_amount,
    description = p_new_description,
    updated_at = NOW()
  WHERE id = p_transaction_id;

  -- Update the user's total balance
  UPDATE users
  SET 
    mcloones_bucks = mcloones_bucks + v_amount_difference,
    updated_at = NOW()
  WHERE id = v_user_id;

  RETURN TRUE;
END;
$$;

